CREATE TABLE `announce` (  `startTime` INT(10) UNSIGNED NOT NULL DEFAULT '0' COMMENT '起始时间，以周期时间取整加此时间开始',  `interval` INT(10) UNSIGNED NOT NULL DEFAULT '3600' COMMENT '间隔周期时间',  `color` TINYINT(3) UNSIGNED NOT NULL DEFAULT '0' COMMENT '颜色: 0-白色 1-绿色 2-蓝色 3-紫色 4-橙色 5-暗金 6-红 7-粉',  `endTime` INT(10) UNSIGNED NOT NULL DEFAULT '0' COMMENT '结束时间，小于当前时间表示从现在开始的指定秒数内结束公告，大于当前时间表示指定时间戳结束，0表示无穷发送',  `msg` VARCHAR(4095) NOT NULL DEFAULT '' COMMENT '公告内容',  `link` VARCHAR(255) NOT NULL DEFAULT '' COMMENT '点击公告后跳转的链接' ) COLLATE='utf8_general_ci' ENGINE=InnoDB ROW_FORMAT=DEFAULT;
CREATE TABLE `data_latest` (  `announce` INT(10) UNSIGNED NOT NULL DEFAULT '0' ) COLLATE='utf8_general_ci' ENGINE=InnoDB ROW_FORMAT=DEFAULT;
