ALTER TABLE `task_instance`  CHANGE COLUMN `id` `id` INT(10) UNSIGNED NOT NULL FIRST,  CHANGE COLUMN `taskId` `taskId` INT(10) UNSIGNED NOT NULL AFTER `id`,  CHANGE COLUMN `ownerId` `ownerId` BIGINT(20) UNSIGNED NOT NULL AFTER `taskId`,  CHANGE COLUMN `acceptTime` `acceptTime` INT(10) UNSIGNED NOT NULL COMMENT '任务接受的时间' AFTER `ownerId`,  CHANGE COLUMN `timeBegin` `timeBegin` INT(10) UNSIGNED NOT NULL COMMENT '任务有效开始的时刻\\r\\n如果是每日任务：则接受任务的时候， timeBegin设置成当天的凌晨时间，timeEnd设置成当天的最后时刻\\r\\n普通的接受任务：则timeBegin设置成接受任务的时刻， 即acceptTime' AFTER `step`,  CHANGE COLUMN `completed` `completed` TINYINT(1) UNSIGNED NOT NULL COMMENT '任务是否完成' AFTER `timeEnd`,  CHANGE COLUMN `submit` `submit` TINYINT(1) UNSIGNED NOT NULL COMMENT '务任是否已提交' AFTER `completed`,  CHANGE COLUMN `submitCount` `submitCount` INT(10) UNSIGNED NOT NULL DEFAULT '0' AFTER `submit`;
ALTER TABLE `day_task_instance`  CHANGE COLUMN `id` `id` INT(10) UNSIGNED NOT NULL AUTO_INCREMENT FIRST,  CHANGE COLUMN `loopTask` `loopTask` INT(10) UNSIGNED NOT NULL AFTER `id`,  CHANGE COLUMN `count` `count` SMALLINT(5) UNSIGNED NOT NULL DEFAULT '0' COMMENT '完成次数' AFTER `loopTask`,  CHANGE COLUMN `maxCount` `maxCount` SMALLINT(5) UNSIGNED NOT NULL DEFAULT '0' AFTER `count`,  CHANGE COLUMN `maxFlushQualityCount` `maxFlushQualityCount` SMALLINT(5) UNSIGNED NOT NULL DEFAULT '0' AFTER `maxCount`,  CHANGE COLUMN `preTaskId` `preTaskId` INT(10) UNSIGNED NOT NULL DEFAULT '0' AFTER `maxFlushQualityCount`,  CHANGE COLUMN `preFlushTime` `preFlushTime` INT(10) UNSIGNED NOT NULL DEFAULT '0' COMMENT '上次刷新时间' AFTER `preTaskId`,  CHANGE COLUMN `preTaskQuality` `preTaskQuality` TINYINT(1) UNSIGNED NOT NULL DEFAULT '0' COMMENT '上次刷新任务颜色：\\r\\n1 白色\\r\\n2 绿色\\r\\n3 蓝色\\r\\n4 紫色\\r\\n5 橙色' AFTER `preFlushTime`,  CHANGE COLUMN `autoCompletedTaskId` `autoCompletedTaskId` INT(10) UNSIGNED NOT NULL DEFAULT '0' COMMENT '自动完成玩的任务编号，如果是0， 表示没有' AFTER `preTaskQuality`,  CHANGE COLUMN `autoCompletedTaskAcceptTime` `autoCompletedTaskAcceptTime` INT(10) UNSIGNED NOT NULL DEFAULT '0' COMMENT '自动完成任务接受时间' AFTER `autoCompletedTaskId`,  CHANGE COLUMN `ownerId` `ownerId` BIGINT(20) UNSIGNED NOT NULL AFTER `autoCompletedTaskAcceptTime`,  CHANGE COLUMN `currDay` `currDay` INT(10) UNSIGNED NOT NULL COMMENT '当天时间' AFTER `ownerId`;
