UPDATE `trade` SET `tradeName` = (SELECT `id` FROM `player` WHERE `name` = `tradeName`);
ALTER TABLE `trade`  CHANGE COLUMN `tradeId` `tradeId` INT(10) UNSIGNED NOT NULL FIRST,  CHANGE COLUMN `ownerId` `ownerId` BIGINT(20) UNSIGNED NOT NULL AFTER `tradeId`,  CHANGE COLUMN `tradeSide` `tradeSide` TINYINT(1) UNSIGNED NOT NULL COMMENT '0:交易方 1:被交易方' AFTER `ownerId`,  CHANGE COLUMN `tradeStatus` `tradeStatus` TINYINT(1) UNSIGNED NOT NULL AFTER `tradeSide`,  CHANGE COLUMN `tradeName` `tradeTo` BIGINT(20) UNSIGNED NOT NULL AFTER `tradeStatus`,  CHANGE COLUMN `tradeTime` `tradeTime` INT(10) UNSIGNED NOT NULL AFTER `tradeTitle`,  CHANGE COLUMN `coin` `coin` INT(10) UNSIGNED NOT NULL DEFAULT '0' AFTER `tradeTime`,  CHANGE COLUMN `gold` `gold` INT(10) UNSIGNED NOT NULL DEFAULT '0' AFTER `coin`;
ALTER TABLE `user_id_name_mapping`  CHANGE COLUMN `id` `id` BIGINT(20) UNSIGNED NOT NULL AUTO_INCREMENT FIRST,  DROP INDEX `name`,  ADD INDEX `name` (`name`);
